-- ----------------------------
-- View structure for v_blog
-- ----------------------------
DROP VIEW IF EXISTS `v_blog`;
CREATE   VIEW `v_blog` AS select `blog`.`id_blog` AS `id_blog`,`blog`.`tanggal` AS `tanggal`,`blog`.`judul` AS `judul`,`blog`.`headline` AS `headline`,`blog`.`isi` AS `isi`,`blog`.`waktu` AS `waktu`,`blog`.`status` AS `status`,`blog`.`url` AS `url`,`v_kelas`.`nama_jenjang` AS `nama_jenjang`,`v_kelas`.`nama_kelas` AS `nama_kelas`,`v_kelas`.`id_kelas` AS `id_kelas`,`kategori`.`id_kategori` AS `id_kategori`,`kategori`.`nama_kategori` AS `nama_kategori`,`mapel`.`nama_mapel` AS `nama_mapel`,`mapel`.`id_mapel` AS `id_mapel`,`username`.`nama` AS `nama`,`username`.`id_user` AS `id_user`,`blog`.`link_youtube` AS `link_youtube`,`blog`.`gambar` AS `gambar` from ((((`blog` left join `v_kelas` on((`blog`.`id_kelas` = `v_kelas`.`id_kelas`))) left join `kategori` on((`blog`.`id_kategori` = `kategori`.`id_kategori`))) left join `mapel` on((`blog`.`id_mapel` = `mapel`.`id_mapel`))) left join `username` on((`blog`.`id_aktor` = `username`.`id_user`))) ; ;

-- ----------------------------
-- View structure for v_kelas
-- ----------------------------
DROP VIEW IF EXISTS `v_kelas`;
CREATE   VIEW `v_kelas` AS select `kelas`.`id_kelas` AS `id_kelas`,`kelas`.`nama_kelas` AS `nama_kelas`,`jenjang`.`nama_jenjang` AS `nama_jenjang`,`jenjang`.`id_jenjang` AS `id_jenjang` from (`kelas` join `jenjang` on((`kelas`.`id_jenjang` = `jenjang`.`id_jenjang`))) ; ;

-- ----------------------------
-- View structure for v_service
-- ----------------------------
DROP VIEW IF EXISTS `v_service`;
CREATE   VIEW `v_service` AS select `service`.`id_service` AS `id_service`,`service`.`gambar` AS `gambar`,`service`.`nama` AS `nama`,`service`.`headline` AS `headline`,`service`.`keterangan` AS `keterangan`,`service`.`id_kategori` AS `id_kategori`,`kategori`.`nama_kategori` AS `nama_kategori` from (`service` join `kategori` on((`kategori`.`id_kategori` = `service`.`id_kategori`))) ; ;

-- ----------------------------
-- View structure for v_soal
-- ----------------------------
DROP VIEW IF EXISTS `v_soal`;
CREATE   VIEW `v_soal` AS select `v_kelas`.`nama_kelas` AS `nama_kelas`,`v_kelas`.`nama_jenjang` AS `nama_jenjang`,`tbl_soal`.`id` AS `id`,`tbl_soal`.`soal` AS `soal`,`tbl_soal`.`a` AS `a`,`tbl_soal`.`b` AS `b`,`tbl_soal`.`c` AS `c`,`tbl_soal`.`d` AS `d`,`tbl_soal`.`jawaban` AS `jawaban`,`tbl_soal`.`gambar` AS `gambar` from (`tbl_soal` join `v_kelas` on((`tbl_soal`.`id_kelas` = `v_kelas`.`id_kelas`))) ; ;
